// ===================== NORMALIZAR DATOS =====================
// Compatible con Webhook + HTML Alternativas + Merge directo
// Formato: hora 24h, fecha DD/MM/YYYY

const TZ = 'America/Santiago';
const q = $json;

// Detecta si vienen dentro de "query" (Webhook)
const src = q.query ? q.query : q;

// Helpers
function toISO(val) {
  if (!val) return '';
  const d = new Date(val);
  return isNaN(d) ? String(val) : d.toISOString();
}

// Convertir a partes locales
function toLocalParts(dtStr) {
  if (!dtStr) return { fecha: '', hora: '' };
  const d = new Date(dtStr);
  if (isNaN(d)) return { fecha: '', hora: '' };

  // Fecha en formato DD/MM/YYYY
  const dia = String(d.getDate()).padStart(2, '0');
  const mes = String(d.getMonth() + 1).padStart(2, '0');
  const anio = d.getFullYear();
  const fecha = `${dia}/${mes}/${anio}`;

  // Hora en formato 24h (HH:mm)
  const hora = d.toLocaleTimeString('es-CL', {
    timeZone: TZ,
    hour: '2-digit',
    minute: '2-digit',
    hour12: false, // <- evita AM/PM
  });

  return { fecha, hora };
}

// Calcular fechas legibles
const startIso = toISO(src.start);
const endIso = toISO(src.end);
const { fecha: fechaLarga, hora: inicioStr } = toLocalParts(startIso);
const { hora: finStr } = toLocalParts(endIso);

// Resultado unificado
return [{
  json: {
    // --- Identificadores y correo ---
    reservaId: src.reservaId || src.ReservID || src.ReservaID || src.reservationId || 'SIN-ID',
    email: src.email || src.correo || src.to || 'sin-correo@demo.local',

    // --- Datos del solicitante ---
    nombre: src.nombre || src.solicitante || '',
    area: src.area || '',
    participantes: Number(src.participantes || 0),
    descripcion: src.descripcion || '',

    // --- Información de evento ---
    summary: src.summary || 'Reunión',
    calendarId: src.calendarId || '',
    roomName: src.room || '',

    // --- Fechas ISO y texto ---
    startIso,
    endIso,
    fechaLarga,  // ahora formato DD/MM/YYYY
    inicioStr,   // formato HH:mm
    finStr       // formato HH:mm
  }
}];

