const r = $json;

// —— helpers
function pad(n){ return String(n).padStart(2,'0'); }
function parseHora(str){
  const parts = String(str || '').trim().split(':');
  return { hh: pad(+parts[0] || 0), mm: pad(+parts[1] || 0) };
}
function parseFechaSheets(v){
  if (v == null) return null;
  if (v instanceof Date && !isNaN(v)) return v;
  if (typeof v === 'number') {
    const epoch = Date.UTC(1899,11,30);
    return new Date(epoch + Math.round(v * 86400000));
  }
  const s = String(v).trim();
  const asDate = new Date(s);
  if (!isNaN(asDate)) return asDate;
  const m = s.match(/^(\d{1,2})[\/\-](\d{1,2})[\/\-](\d{4})$/);
  if (m) {
    const dd = +m[1], mm = +m[2] - 1, yyyy = +m[3];
    return new Date(yyyy, mm, dd);
  }
  return null;
}

// —— toma la fecha
const rawFecha = r['Fecha'] ?? r['Fecha de la reunión'] ?? r['fecha'] ?? r['Date'] ?? r['Marca temporal'] ?? null;
const fecha = parseFechaSheets(rawFecha) || new Date();

const hi = parseHora(r['Inicio'] ?? r['Hora de inicio']);
const hf = parseHora(r['Fin']    ?? r['Hora de Termino']);

const yyyy = fecha.getFullYear();
const mm   = pad(fecha.getMonth()+1);
const dd   = pad(fecha.getDate());

// Nombres de meses en español
const meses = [
  'enero','febrero','marzo','abril','mayo','junio',
  'julio','agosto','septiembre','octubre','noviembre','diciembre'
];

// Nueva variable con formato “20 de septiembre del 2025”
const fechaLarga = `${dd} de ${meses[fecha.getMonth()]} del ${yyyy}`;

// huso horario fijo
const tzOffset = '-03:00';
const startIso = `${yyyy}-${mm}-${dd}T${hi.hh}:${hi.mm}:00${tzOffset}`;
const endIso   = `${yyyy}-${mm}-${dd}T${hf.hh}:${hf.mm}:00${tzOffset}`;

return [{
  json: {
    startIso,
    endIso,
    summary: `Reunión: ${r['Nombre'] || r['Nombre del solicitante'] || ''}`,
    description: `Área: ${r['Área'] || r['Área/Departamento'] || ''}`,
    requesterEmail: r['Correo'] || r['Correo electrónico'] || '',
    participantes: Number(r['N° Participantes'] ?? r['Número de participantes'] ?? 0),

    // para la hoja Registro:
    nombre: r['Nombre'] || r['Nombre del solicitante'] || '',
    correo: r['Correo'] || r['Correo electrónico'] || '',
    area:   r['Área'] || r['Área/Departamento'] || '',
    fechaStr: `${dd}-${mm}-${yyyy}`,
    fechaLarga,  // ← aquí queda disponible
    inicioStr: `${hi.hh}:${hi.mm}`,
    finStr: `${hf.hh}:${hf.mm}`,

    ReservaID: `res-${Date.now()}-${Math.random().toString(16).slice(2,8)}`
  }
}];

const reserva = $items()[0].json;
const rooms   = $items().slice(1).map(i => i.json);

const capReq = Number(reserva.participantes || 0);

const roomsToUse = rooms
  .map(r => ({
    room: String(r.Sala || '').trim(),
    calendarId: String(r.ID || '').trim(),
    capacity: Number(r.Capacidad || 0),
    torre: String(r.Ubicacion || '').trim()   // ← añadimos la torre
  }))
  .filter(r => r.calendarId)
  .filter(r => r.capacity >= capReq);

return roomsToUse.map(r => ({
  json: {
    ...reserva,
    room: r.room,
    calendarId: r.calendarId,
    capacity: r.capacity,
    torre: r.torre                       // ← heredamos torre al flujo
  }
}));

// Items del Merge: [ base, ...eventos ]
const all = $items();
const base = all[0]?.json || {};
const events = all.slice(1).map(i => i.json).filter(e => e && (e.start || e.end));

const reqStart = Date.parse(base.startIso);
const reqEnd   = Date.parse(base.endIso);

function toRange(e){
  const s  = e.start?.dateTime || (e.start?.date && (e.start.date + 'T00:00:00Z'));
  const en = e.end?.dateTime   || (e.end?.date   && (e.end.date   + 'T23:59:59Z'));
  if (!s || !en) return null;
  return { start: Date.parse(s), end: Date.parse(en) };
}

const ranges = events.map(toRange).filter(Boolean);
const isFree = !ranges.some(b => Math.max(reqStart, b.start) < Math.min(reqEnd, b.end));

return [{
  json: {
    ...base,                 // <- aquí vuelven ReservaID, room, calendarId, startIso, endIso
    checkedEvents: events.length,
    isFree
  }
}];

// n8n Code/Function: GenerarAlternativasDesdeLoopDone (parche flex + meta + correo)
// Soporta: startIso/endIso, calendarId, room | roomName
// Propaga meta para el HTML/Webhook: descripcion/description, correo, area, ReservID, nombre, participantes.

const DEFAULT_MIN_MINUTES = 30;
const DEFAULT_MAX_MINUTES = 120;
const OFFSETS_MINUTES = [-180, -120, -60, 60, 120, 180];
const MAX_OPTIONS_PER_ROOM = 8;

const STEP_LABEL = (m)=> (m<0? `${Math.abs(m)} min antes` : `${m} min después`);

function toDate(x) {
  if (!x) return null;
  if (typeof x === 'object' && (x.date || x.dateTime)) {
    return new Date(x.dateTime || x.date);
  }
  return new Date(x);
}
function toISO(dt) { return new Date(dt).toISOString(); }
function clampDuration(mins, minAllowed, maxAllowed) {
  return Math.max(minAllowed, Math.min(maxAllowed, mins));
}
function overlaps(aStart, aEnd, bStart, bEnd) {
  return (aStart < bEnd) && (bStart < aEnd);
}
function sameDay(d1, d2) {
  return d1.getFullYear() === d2.getFullYear() &&
         d1.getMonth() === d2.getMonth() &&
         d1.getDate() === d2.getDate();
}
function shiftMinutes(date, mins) { const d = new Date(date); d.setMinutes(d.getMinutes()+mins); return d; }
function addDays(date, days) { const d = new Date(date); d.setDate(d.getDate()+days); return d; }

function normalizeBusyIntervals(j) {
  if (Array.isArray(j.busyIntervals)) {
    return j.busyIntervals
      .map(b => ({ start: toDate(b.start), end: toDate(b.end) }))
      .filter(b => b.start && b.end && b.start < b.end);
  }
  const events = Array.isArray(j.events) ? j.events : [];
  return events
    .map(ev => {
      const s = toDate(ev.start?.dateTime || ev.start?.date);
      const e = toDate(ev.end?.dateTime || ev.end?.date);
      return (s && e && s < e) ? { start: s, end: e } : null;
    })
    .filter(Boolean);
}

// ---- helpers correo ----
function getRequesterEmail(localJson) {
  // 1) del propio ítem
  let mail = localJson.correo || localJson.email || localJson.requesterEmail || localJson.solicitanteEmail;
  // 2) intenta leer del nodo de formulario si existe (ajusta el nombre si es distinto)
  if (!mail) {
    try {
      const form = $items('DatosFormulario', 0, 0);
      if (form && form.json) {
        mail = form.json.correo || form.json.email || form.json.mail;
      }
    } catch (e) { /* si no existe el nodo, no pasa nada */ }
  }
  // 3) limpieza simple
  if (typeof mail === 'string') {
    mail = mail.trim();
    if (mail === '') mail = undefined;
  }
  return mail;
}

const out = [];

for (const item of items) {
  const j = item.json || {};

  // ---- Adaptadores de campos (flex) ----
  const roomId   = j.roomId || j.calendarId || j.calendar || j.calendario || j.calendarid;
  const roomName = j.roomName || j.room || j.name || String(roomId || 'Sala');

  // ---- Meta a propagar (tal como viene en tu flujo) ----
  const descripcion  = j.descripcion || j.description || '';          // ej: "Área: Informatica"
  const area         = j.area || j.Area || j['área'] || j['Área'] || j.departamento || j.Departamento || '';
  const ReservID     = j.ReservID || j.reservationId || j.idReserva || j.IDReserva || j.id || j.ReservaID || '';
  const nombre       = j.nombre || j.solicitanteNombre || j.Nombre || '';
  const participantes= j.participantes || j.Participantes || j.asistentes || j.participants || '';

  // correo del solicitante (se propagará en la salida)
  const correo = getRequesterEmail(j) || j.correo || '';

  let reqStart = null, reqEnd = null;

  // 1) request.start/end (si existiera)
  if (j.request && (j.request.start || j.request.end)) {
    reqStart = toDate(j.request.start);
    reqEnd   = toDate(j.request.end);
  }
  // 2) requestStart/requestEnd
  if (!reqStart && j.requestStart) reqStart = toDate(j.requestStart);
  if (!reqEnd   && j.requestEnd)   reqEnd   = toDate(j.requestEnd);
  // 3) startIso/endIso
  if (!reqStart && j.startIso) reqStart = toDate(j.startIso);
  if (!reqEnd   && j.endIso)   reqEnd   = toDate(j.endIso);
  // 4) start/end directos
  if (!reqStart && j.start) reqStart = toDate(j.start);
  if (!reqEnd   && j.end)   reqEnd   = toDate(j.end);

  // Si no hay hora o no hay sala, saltamos
  if (!reqStart || !reqEnd || !(reqStart < reqEnd) || !roomId) {
    continue;
  }

  // Restricciones
  const minMinutes = Number.isFinite(Number(j.constraints?.minMinutes))
    ? Number(j.constraints.minMinutes) : DEFAULT_MIN_MINUTES;
  const maxMinutes = Number.isFinite(Number(j.constraints?.maxMinutes))
    ? Number(j.constraints.maxMinutes) : DEFAULT_MAX_MINUTES;

  const requestedDurationMin = Math.round((reqEnd - reqStart) / 60000);
  const slotDurationMin = clampDuration(requestedDurationMin, minMinutes, maxMinutes);

  // Busy conocido (si no hay, lista vacía → luego verificas con GCal)
  const busy = normalizeBusyIntervals(j);

  // Candidatos mismo día
  const sameDayCandidates = [];
  for (const off of OFFSETS_MINUTES) {
    const s = shiftMinutes(reqStart, off);
    if (!sameDay(s, reqStart)) continue;
    const e = shiftMinutes(s, slotDurationMin);
    sameDayCandidates.push({ start: s, end: e, offset: off, dayShift: 0 });
  }

  // Candidatos +1 día
  const nextDayBase = addDays(reqStart, 1);
  const nextDayCandidates = [];
  for (const off of OFFSETS_MINUTES) {
    const s = shiftMinutes(nextDayBase, off);
    const e = shiftMinutes(s, slotDurationMin);
    nextDayCandidates.push({ start: s, end: e, offset: off, dayShift: 1 });
  }

  const allCandidates = [...sameDayCandidates, ...nextDayCandidates];

  // Filtrar contra busy (si busy=[], pasan todos)
  const freeOptions = [];
  outer:
  for (const c of allCandidates) {
    for (const b of busy) {
      if (overlaps(c.start, c.end, b.start, b.end)) continue outer;
    }
    freeOptions.push(c);
    if (freeOptions.length >= MAX_OPTIONS_PER_ROOM) break;
  }

  for (const opt of freeOptions) {
    const prettyLabel = (opt.dayShift === 0)
      ? `Mismo día, ${STEP_LABEL(opt.offset)}`
      : `+1 día, ${STEP_LABEL(opt.offset)}`;

    out.push({
      json: {
        // ---- Identificación de sala ----
        calendarId: roomId,
        roomId,
        roomName,

        // ---- Meta propagada (para HTML/Webhook) ----
        descripcion,
        description: descripcion,                 // alias en inglés
        area,
        ReservID,
        reservationId: ReservID,                  // alias
        nombre,
        solicitanteNombre: nombre,                // alias
        participantes,

        // ---- Contacto ----
        correo,
        to: correo,                               // alias por comodidad

        // ---- Opción propuesta ----
        option: {
          start: toISO(opt.start),
          end: toISO(opt.end),
          minutes: slotDurationMin,
          label: prettyLabel,
          offsetsMinutes: opt.offset,
          dayShift: opt.dayShift,
        },

        // ---- Datos de la solicitud original ----
        request: {
          start: toISO(reqStart),
          end: toISO(reqEnd),
          requestedMinutes: requestedDurationMin,
          appliedMinutes: slotDurationMin,
          minMinutes,
          maxMinutes,
        },
      },
    });
  }
}

return out;

// Code: Build HTML Alternativas (Run Once for All Items)
// Con soporte robusto para extraer meta (área, participantes, solicitante, id)
// Salida: { to, subject, htmlBody }

const bookingEmail = 'correoparapracticar1@gmail.com';
const TZ = 'America/Santiago';
const CONFIRM_PREFIX = 'CONFIRMAR:';
const WEBHOOK_URL = 'http://localhost:5678/webhook-test/confirmar-reserva';

// ====== Helpers ======
function toLocal(dtStr) {
  const d = new Date(dtStr);
  const fecha = d.toLocaleDateString('es-CL', { timeZone: TZ, weekday:'long', day:'2-digit', month:'long', year:'numeric' });
  const hora  = d.toLocaleTimeString('es-CL', { timeZone: TZ, hour:'2-digit', minute:'2-digit' });
  return { fecha, hora };
}
function groupBy(arr, keyFn) {
  const m = new Map();
  for (const x of arr) {
    const k = keyFn(x);
    if (!m.has(k)) m.set(k, []);
    m.get(k).push(x);
  }
  return m;
}
function uniqEmails(list) {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return Array.from(new Set(list.filter(e => e && emailRegex.test(e.trim())).map(e => e.trim())));
}
function enc(v) { return encodeURIComponent(v == null ? '' : String(v)); }
function fromNode(name){ try{ return $items(name,0,0)?.json || {}; }catch{ return {}; } }
function firstNonEmpty(...vals){ for (const v of vals){ if (v!=null && String(v).trim()!=='') return String(v).trim(); } return ''; }

// ====== Normalización de items ======
const libres = items.map(i => i.json).filter(j => j?.option?.start && j?.option?.end);
libres.sort((a,b) => new Date(a.option.start) - new Date(b.option.start));

// ====== Destinatarios ======
const emailsFromItems = items.map(i => (i.json.to || i.json.correo || '')).filter(Boolean);
let ref; try { ref = $items('SET1', 0, 0); } catch { ref = null; }
const fallback = (ref?.json?.correo || ref?.json?.email || '').trim();
const toList = uniqEmails([...emailsFromItems, fallback]);
const to = toList.join(', ');

// ===== ULTRA-ROBUSTO: leer meta desde raíz, request, option y nodos cercanos =====
const src   = (items?.[0]?.json) || {};
const roots = [
  src,
  src.request || {},
  src.option  || {},
  src.data    || {},
  fromNode('DatosCode'),
  fromNode('DatosFormulario'),
  fromNode('SET1'),
  fromNode('guardarRegistro'),
];

// helper para buscar en múltiples objetos y variantes de clave
function pick(keys){
  for (const r of roots){
    for (const k of keys){
      if (r?.[k] != null && String(r[k]).trim()!=='') return String(r[k]).trim();
      if (k.includes('.')){
        const parts = k.split('.');
        let cur = r;
        for (const p of parts){ cur = cur?.[p]; }
        if (cur != null && String(cur).trim()!=='') return String(cur).trim();
      }
    }
  }
  return '';
}

// ==== AQUÍ SE DEFINEN TUS 4 CAMPOS ====
const area = pick(['area','Area','área','Área','departamento','Departamento','request.area','request.departamento']);
const participantes = pick(['participantes','Participantes','asistentes','participants','request.participantes','request.asistentes']);
const solicitanteNombre = pick(['solicitanteNombre','nombre','Nombre','solicitante','request.solicitanteNombre','request.nombre']);
const reservationId = firstNonEmpty(
  pick(['ReservID','reservationId','reservaId','idReserva','IDReserva','id','ID','request.reservationId','request.id']),
  'RID'
);

// ====== Asunto ======
const reqStart = libres[0]?.request?.start || null;
const reqLocal = reqStart ? toLocal(reqStart) : null;
const subject = (libres.length > 0)
  ? `Alternativas de sala — ${reqLocal ? `${reqLocal.hora}, ${reqLocal.fecha}` : `${libres.length} opciones`} — ${reservationId}`
  : `Sin alternativas disponibles — ${reservationId}`;

// ====== Construcción HTML ======
const byRoom = groupBy(libres, j => j.roomName || j.roomId || 'Sala');
let opIndex = 1;
let cardsHTML = '';

for (const [room, arr] of byRoom.entries()) {
  cardsHTML += `
    <h2 style="font-size:16px; margin:24px 0 8px; color:#222;">
      <span style="display:inline-block;width:8px;height:8px;border-radius:999px;background:#6c5ce7;margin-right:8px;vertical-align:middle"></span>
      ${room} <span style="color:#666; font-weight:400; font-size:12px;">(${arr.length} opción${arr.length>1?'es':''})</span>
    </h2>`;

  for (const j of arr) {
    const s = toLocal(j.option.start);
    const e = toLocal(j.option.end);
    const dur = j.option.minutes || Math.round((new Date(j.option.end) - new Date(j.option.start))/60000);
    const roomId = j.roomId || '';
    const calendarId = j.calendarId || j.roomCalendarId || '';
    const code = `${reservationId}-OP${String(opIndex).padStart(2,'0')}`;
    j.confirmCode = code;

    const webhookLink =
      `${WEBHOOK_URL}`
      + `?code=${enc(code)}`
      + `&reservationId=${enc(reservationId)}`
      + `&room=${enc(room)}`
      + `&roomId=${enc(roomId)}`
      + `&calendarId=${enc(calendarId)}`
      + `&start=${enc(j.option.start)}`
      + `&end=${enc(j.option.end)}`
      + `&minutes=${enc(dur)}`
      + `&requestStart=${enc(reqStart || '')}`
      + `&to=${enc(to)}`
      + `&solicitante=${enc(solicitanteNombre)}`
      + `&area=${enc(area)}`
      + `&participantes=${enc(participantes)}`;

    cardsHTML += `
      <div class="option">
        <div class="option-title">${j.option.label || 'Alternativa'} — <span style="font-weight:700;">Código: ${code}</span></div>
        <div class="option-details">
          <div><strong>Fecha:</strong> ${s.fecha}</div>
          <div><strong>Horario:</strong> ${s.hora} — ${e.hora}</div>
          <div><strong>Duración:</strong> ${dur} min</div>
          ${j.capacidad ? `<div><strong>Capacidad:</strong> ${j.capacidad}</div>` : ``}
          ${j.ubicacion ? `<div><strong>Ubicación:</strong> ${j.ubicacion}</div>` : ``}
        </div>
        <a href="${webhookLink}" target="_blank" class="btn-confirm">Elegir esta sala (confirmar)</a>
      </div>`;
    opIndex++;
  }
}

// ====== Resumen ======
const resumenHTML = `
  <div class="info-grid">
    <div class="info-box"><span>Área / Departamento</span><strong>${area || '(sin área)'}</strong></div>
    <div class="info-box"><span>Participantes</span><strong>${participantes || '(sin participantes)'}</strong></div>
    <div class="info-box"><span>Solicitante</span><strong>${solicitanteNombre || '(sin nombre)'}</strong></div>
    <div class="info-box"><span>ID de Reserva</span><strong>${reservationId}</strong></div>
  </div>
`;

// ====== HTML Final ======
const htmlBody = `<!doctype html>
<html lang="es">
<head>
<meta charset="utf-8">
<title>${subject}</title>
<style>
  body {
    background-color: #F9F6EE;
    font-family: 'Segoe UI', Roboto, Arial, sans-serif;
    margin: 0;
    padding: 40px 16px;
    color: #111;
  }
  .card {
    max-width: 720px;
    margin: 0 auto;
    background: #ffffff;
    border-radius: 18px;
    overflow: hidden;
    box-shadow: 0 4px 20px rgba(0,0,0,0.25);
  }
  .header {
    background: linear-gradient(90deg, #f4b400 0%, #fcd34d 100%);
    color: #1f1f1f;
    padding: 24px;
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
  .header h1 {
    margin: 0;
    font-size: 20px;
    font-weight: 700;
  }
  .estado {
    background: rgba(255,255,255,0.25);
    color: #111;
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 13px;
    font-weight: 600;
    text-transform: uppercase;
  }
  .content {
    padding: 28px;
  }
  .intro {
    font-size: 15px;
    color: #222;
    margin-bottom: 20px;
    line-height: 1.5;
  }
  .info-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 14px;
    margin-bottom: 24px;
  }
  .info-box {
    border: 1px solid #e6e6eb;
    border-radius: 10px;
    padding: 12px 16px;
    background: #fafafa;
  }
  .info-box span {
    display: block;
    font-size: 12px;
    color: #666;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    margin-bottom: 4px;
  }
  .info-box strong {
    font-size: 15px;
    color: #111;
  }
  .option {
    border: 1px solid #eee;
    border-radius: 10px;
    padding: 18px 20px;
    margin-bottom: 16px;
    background: #f9f9f9;
  }
  .option-title {
    font-weight: 700;
    margin-bottom: 8px;
    color: #1a1a1a;
  }
  .option-details {
    font-size: 14px;
    color: #333;
    line-height: 1.5;
  }
  .btn-confirm {
    display: inline-block;
    background-color: #111827;
    color: #fff !important;
    text-decoration: none;
    font-weight: 600;
    border-radius: 8px;
    padding: 12px 18px;
    margin-top: 14px;
    transition: background 0.2s ease-in-out;
  }
  .btn-confirm:hover {
    background-color: #1e293b;
  }
  .footer {
    font-size: 12px;
    color: #bbb;
    text-align: center;
    padding: 20px;
  }
</style>
</head>
<body>
  <div class="card">
    <div class="header">
      <h1>Alternativas disponibles</h1>
      <div class="estado">En espera de respuesta</div>
    </div>
    <div class="content">
      <p class="intro">Hola <strong>${solicitanteNombre || 'Usuario'}</strong>, tu solicitud no encontró disponibilidad exacta. 
      A continuación se muestran las salas alternativas disponibles. Elige la que más te acomode para confirmar tu reserva.</p>
      ${resumenHTML}
      ${cardsHTML || `<p style="color:#888;">No se encontraron opciones disponibles.</p>`}
    </div>
    <div class="footer">
      * Horarios expresados en <b>America/Santiago (GMT-3)</b>.<br/>
      Si ninguna opción te acomoda, responde a este correo indicando un nuevo horario.
    </div>
  </div>
</body>
</html>`;

// ====== Validación ======
if (!to) {
  return [{ json: { to:'', subject:`[REVISAR] ${subject}`, htmlBody, warning:'No se encontró correo (to)' } }];
}

// ====== Salida ======
return [{
  json: {
    to,
    subject,
    htmlBody,
    totalOptions: libres.length,
    area,
    participantes,
    solicitanteNombre,
    reservationId
